import React, { Component } from 'react';
import AdaptiveCard from 'react-adaptivecards';


class AdaptiveUserCard extends Component {

// name, siteWeb, email, phone

 payloads = () =>{

    return(

        {
    
            "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
            "type": "AdaptiveCard",
            "version": "1.0",
            "body": [
              {
                "type": "Container",
                "items": [
                  {
                    "type": "TextBlock",
                    "text": "Carte utilisateur",
                    "weight": "bolder",
                    "size": "medium"
                  },
                  {
                    "type": "ColumnSet",
                    "columns": [
                      {
                        "type": "Column",
                        "width": "auto",
                        "items": [
                          {
                            "type": "Image",
                            "url": "https://pbs.twimg.com/profile_images/3647943215/d7f12830b3c17a5a9e4afcc370e3a37e_400x400.jpeg",
                            "size": "small",
                            "style": "person"
                          }
                        ]
                      },
                      {
                        "type": "Column",
                        "width": "stretch",
                        "items": [
                          {
                            "type": "TextBlock",
                            "text": {name},
                            "weight": "bolder",
                            "wrap": true
                          },
                         
                        ]
                      }
                    ]
                  }
                ]
              },
              {
                "type": "Container",
                "items": [
                 
                  {
                    "type": "FactSet",
                    "facts": [
                    
                      {
                        "title": "Site web:",
                        "value": {siteWeb}
                      },
                      {
                        "title": "E-mail:",
                        "value": {mail}
                      },
                      {
                        "title": "Mobile:",
                        "value": {phone}
                      }
                    ]
                  }
                ]
              }
            ]
          
          }

    );

 }





  constructor(props) {
    super(props);

    this.state = {
      loading: true,
      result: <AdaptiveCard style={{width: '400px', border: '1px solid black'}} payload={() => payloads(name, siteWeb, email, phone)} />,
      trigger: false,
    };

    this.triggetNext = this.triggetNext.bind(this);
  }


  triggetNext() {
    this.setState({ trigger: true }, () => {
      this.props.triggerNextStep();
    });
  }

  render() {
    const result  = this.state.result;

    return (
      <div className="dbpedia" style={{width:'100%'}}>
		{result}
      </div>
    );
  }
}

export default AdaptiveUserCard
