import React, { Component } from 'react';
import AdaptiveCard from 'react-adaptivecards';
import axios from 'axios';


var payloads= 

      {
  
          "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
          "type": "AdaptiveCard",
          "version": "1.0",
          "body": [
            {
              "type": "Container",
              "items": [
                {
                  "type": "TextBlock",
                  "text": "Carte utilisateur",
                  "weight": "bolder",
                  "size": "medium"
                },
                {
                  "type": "ColumnSet",
                  "columns": [
                    {
                      "type": "Column",
                      "width": "auto",
                      "items": [
                        {
                          "type": "Image",
                          "url": "https://pbs.twimg.com/profile_images/3647943215/d7f12830b3c17a5a9e4afcc370e3a37e_400x400.jpeg",
                          "size": "small",
                          "style": "person"
                        }
                      ]
                    },
                    {
                      "type": "Column",
                      "width": "stretch",
                      "items": [
                        {
                          "type": "TextBlock",
                          "text": "{name}",
                          "weight": "bolder",
                          "wrap": true
                        },
                       
                      ]
                    }
                  ]
                }
              ]
            },
            {
              "type": "Container",
              "items": [
               
                {
                  "type": "FactSet",
                  "facts": [
                  
                    {
                      "title": "Site web:",
                      "value": "{siteWeb}"
                    },
                    {
                      "title": "E-mail:",
                      "value": "{mail}"
                    },
                    {
                      "title": "Mobile:",
                      "value": "{phone}"
                    }
                  ]
                }
              ]
            }
          ]
        
        }

     




class AdaptiveUserCard extends Component {

// name, siteWeb, email, phone

  constructor(props) {
    super(props);
    console.log(props)
    this.state = {
      loading: true,
      result: <AdaptiveCard style={{width: '400px', border: '1px solid black'}} payload={ payloads } />,
      trigger: false,
      user:{}
    };

    this.triggetNext = this.triggetNext.bind(this);
  }

  async componentDidUpdate() {
    console.log('didMount' + this.props.id);
   
      await axios.get(`https://jsonplaceholder.typicode.com/users/${this.props.id}`)
        
        .then(response => {

        // create an array of contacts only with relevant data
        const newUser = response.data;
        console.log(newUser)
        // create a new "State" object without mutating 
        // the original State object. 
        // const newState = Object.assign({}, this.state, {
        //   data: newUser
        // });

        // store the new state object in the component's state
        this.setState({
          user : newUser});
        
      })
      .catch(error => console.log(error));
  }
  

  triggetNext() {
    this.setState({ trigger: true }, () => {
      this.props.triggerNextStep();
    });
  }

  render() {
    const result  = this.state.result; 
    console.log(this.state.user)
    return (
      <div className="dbpedia" style={{width:'100%'}}>
		{result}
      </div>
    );
  }
}

export default AdaptiveUserCard
