import AdaptiveUserCard from './AdaptiveUserCard';



class ReviewCard extends Component {
    constructor(props) {
      super(props);
      this.idUser=''
    }
  
    componentWillReceiveProps(prevProps, currentProps) {
        
      const id = this.props.steps.reqUserRes.value; 
      console.log(this.props)
      this.idUser = id
      console.log(prevProps)
      console.log(currentProps)
      if (prevProps !== currentProps) {
        return true
      }
      return false
    }
  
  
    render() {
     const visible = this.idUser? (<p></p>):(<AdaptiveUserCard id = {this.idUser} />);
      //return <AdaptiveUserCard id = {this.idUser} />
       {visible}              
    }
  }

