import React, { Component } from 'react';
import Review from './Review';
import AdaptiveCardWrapper from './AdaptiveCardWrapper'
import AdaptiveUserCard from './AdaptiveUserCard';
import ReviewCard from './ReviewCard';
import '../index.css';
import PropTypes from 'prop-types';
import ChatBot from 'react-simple-chatbot';
import botAvatar from '../CapGeImage.jpg';






AdaptiveCardWrapper.propTypes = {
  steps: PropTypes.object,
  triggerNextStep: PropTypes.func,
};

AdaptiveCardWrapper.defaultProps = {
  steps: undefined,
  triggerNextStep: undefined,
};

Review.propTypes = {
  steps: PropTypes.object,
};

Review.defaultProps = {
  steps: undefined,
};

AdaptiveUserCard.propTypes = {
  steps: PropTypes.object,
  triggerNextStep: PropTypes.func,
};

AdaptiveUserCard.defaultProps = {
  steps: undefined,
  triggerNextStep: undefined,
}







class SimpleForm extends Component {

    state={
      id:1,
      user:{}
    }
 
  
    
    //componentDidUpdate(){
        // let payload = {
        //     token: "2u2q0SJJ70n8x-iHcoTmgw",
        //     data: {
        //       name: "nameFirst",
        //       email: "internetEmail",
        //       phone: "phoneHome",
        //       _repeat: 10
        //     }
        // };
        
        // axios({
        //   method: "post",
        //   url: `https://jsonplaceholder.typicode.com/users/${this.state.id}`,
        //   data: payload
        // }).then(resp => {
        //     console.log('id ' + this.state.id);
        //   this.setState({
        //       data: resp.data[this.state.id],
        //   })
        //   console.log('user ' + this.state.data)
        //   return true;
        // });
        // console.log('id= ' +this.state.id)
        // var test = false;
        // if(this.state.id){
        //   console.log('on est rentree avec un id = '+this.state.id)
        //   fetch(`https://jsonplaceholder.typicode.com/users/${this.state.id}`)
        //    .then(response => response.json())
        //       .then(json => {this.setState(
        //         {data:json}
        //         );
        //         console.log(this.state.data)
                
        //       }
        //         )


        // }
    //}
      



   
    render() {
      return (
        <ChatBot
          steps={[
            {
              id: '1',
              message: 'Bonjour, je suis votre assistant virtuel, Donnez votre nom?',
              trigger: 'name',
            },
            {
              id: 'name',
              user: true,
              trigger: '3',
            },
            {
              id: '3',
              message: ' {previousValue}! Quel est votre sexe?',
              trigger: 'gender',
            },
            {
              id: 'gender',
              options: [
                { value: 'male', label: 'Homme', trigger: '5' },
                { value: 'female', label: 'Femme', trigger: '5' },
              ],
            },
            {
              id: '5',
              message: 'Quel est votre âge?',
              trigger: 'age',
            },
            {
              id: 'age',
              user: true,
              trigger: '7',
              validator: (value) => {
                if (isNaN(value)) {
                  return 'La valeur doit être un nombre';
                } else if (value < 0) {
                  return 'La valeur doit être positive';
                } else if (value > 120) {
                  return `${value}? Ok!`;
                }
  
                return true;
              },
            },
            {
              id: '7',
              message: 'Voilà un petit sommaire',
              trigger: 'review',
            },
            {
              id: 'review',
              component: <Review />,
              asMessage: true,
              trigger: 'update',
            },
            {
              id: 'update',
              message: 'Voulez vous modifier quelques champs?',
              trigger: 'update-question',
            },
            {
              id: 'update-question',
              options: [
                { value: 'yes', label: 'Oui', trigger: 'update-yes' },
                { value: 'no', label: 'Non', trigger: 'wannasee-adaptivecard-question' },
              ],
            },
            {
              id: 'update-yes',
              message: 'Quel champ voulez vous modifier?',
              trigger: 'update-fields',
            },
            {
              id: 'wannasee-adaptivecard-question',
              message: 'Voulez vous voir la carte adaptative?',
              trigger: 'wannasee-adaptivecard',
            },
            {
              id: 'update-fields',
              options: [
                { value: 'name', label: 'Nom', trigger: 'update-name' },
                { value: 'gender', label: 'Sexe', trigger: 'update-gender' },
                { value: 'age', label: 'Age', trigger: 'update-age' },
              ],
            },
            {
              id: 'update-name',
              update: 'name',
              trigger: '7',
            },
            {
              id: 'update-gender',
              update: 'gender',
              trigger: '7',
            },
            {
              id: 'update-age',
              update: 'age',
              trigger: '7',
            },
            {
              id: 'wannasee-adaptivecard',
              options: [
                { value: 'Yes', label: 'Oui', trigger: 'see-adaptivecard' },
                { value: 'No', label: 'Non', trigger: 'stock' },
              ],
            },
            {
              id: 'see-adaptivecard',
              component: <AdaptiveCardWrapper />,
              trigger: 'stock',
            },
            {
                id: 'stock',
                message: ' Voulez - vous  des informations sur des ?',
                trigger: 'stock-option',
            },
            {
                id: 'stock-option',
                options: [
                  { value: 'User', label: 'Utilisateur', trigger: 'user' },
                  { value: 'product', label: 'Produit', trigger: 'end-message' },
                ],
            },
            {
                id: 'user',
                message: " Donnez l'identifiant de l'utilisateur ?",
                trigger: 'reqUserRes',
            },
            {
                id: 'reqUserRes',
                user: true,
                trigger: 'userResult',
                validator: (value) => {
                    if (isNaN(value)) {
                      return 'La valeur doit être un nombre';
                    } else if (value < 0) {
                      return 'La valeur doit être positive';
                    } else if (value > 10) {   
                      return `${value}? est hors de la plage des id!`;
                    } else {
                        return true;
                    }
      
                    
                  },
            },
            {
                id: 'userResult',
                component: <ReviewCard />,
                trigger: 'reqUser',
              },
            {
                id: 'reqUser',
                message: " D'autres informations pour un autre utilisateur ?",
                trigger: 'autreUseragain',
            },
            {
                id: 'autreUseragain',
                options: [
                  { value: 'Yes', label: 'oui', trigger: 'user' },
                  { value: 'No', label: 'Non', trigger: 'end-message' },
                ],
            },
            
            {
              id: 'end-message',
              message: 'Merci , à bientôt!',
              end: true,
            },
          ]}
        botAvatar={botAvatar}/>
      );
    }
  }
export default SimpleForm
