import React, { Component } from 'react';
import Review from './Review';
import AdaptiveCardWrapper from './AdaptiveCardWrapper'
import ReactDOM from 'react-dom';
import '../index.css';
import PropTypes from 'prop-types';
import * as serviceWorker from './serviceWorker';
import ChatBot from 'react-simple-chatbot';
import botAvatar from '../CapGeImage.jpg';




AdaptiveCardWrapper.propTypes = {
  steps: PropTypes.object,
  triggerNextStep: PropTypes.func,
};

AdaptiveCardWrapper.defaultProps = {
  steps: undefined,
  triggerNextStep: undefined,
};


Review.propTypes = {
  steps: PropTypes.object,
};

Review.defaultProps = {
  steps: undefined,
};







class SimpleForm extends Component {
    render() {
      return (
        <ChatBot
          steps={[
            {
              id: '1',
              message: 'What is your name?',
              trigger: 'name',
            },
            {
              id: 'name',
              user: true,
              trigger: '3',
            },
            {
              id: '3',
              message: 'Hi {previousValue}! What is your gender?',
              trigger: 'gender',
            },
            {
              id: 'gender',
              options: [
                { value: 'male', label: 'Male', trigger: '5' },
                { value: 'female', label: 'Female', trigger: '5' },
              ],
            },
            {
              id: '5',
              message: 'How old are you?',
              trigger: 'age',
            },
            {
              id: 'age',
              user: true,
              trigger: '7',
              validator: (value) => {
                if (isNaN(value)) {
                  return 'value must be a number';
                } else if (value < 0) {
                  return 'value must be positive';
                } else if (value > 120) {
                  return `${value}? Come on!`;
                }
  
                return true;
              },
            },
            {
              id: '7',
              message: 'Great! Check out your summary',
              trigger: 'review',
            },
            {
              id: 'review',
              component: <Review />,
              asMessage: true,
              trigger: 'update',
            },
            {
              id: 'update',
              message: 'Would you like to update some field?',
              trigger: 'update-question',
            },
            {
              id: 'update-question',
              options: [
                { value: 'yes', label: 'Yes', trigger: 'update-yes' },
                { value: 'no', label: 'No', trigger: 'wannasee-adaptivecard-question' },
              ],
            },
            {
              id: 'update-yes',
              message: 'What field would you like to update?',
              trigger: 'update-fields',
            },
            {
              id: 'wannasee-adaptivecard-question',
              message: 'Do you want to see an example of adaptivecard?',
              trigger: 'wannasee-adaptivecard',
            },
            {
              id: 'update-fields',
              options: [
                { value: 'name', label: 'Name', trigger: 'update-name' },
                { value: 'gender', label: 'Gender', trigger: 'update-gender' },
                { value: 'age', label: 'Age', trigger: 'update-age' },
              ],
            },
            {
              id: 'update-name',
              update: 'name',
              trigger: '7',
            },
            {
              id: 'update-gender',
              update: 'gender',
              trigger: '7',
            },
            {
              id: 'update-age',
              update: 'age',
              trigger: '7',
            },
            {
              id: 'wannasee-adaptivecard',
              options: [
                { value: 'Yes', label: 'Yes', trigger: 'see-adaptivecard' },
                { value: 'No', label: 'No', trigger: 'end-message' },
              ],
            },
            {
              id: 'see-adaptivecard',
              component: <AdaptiveCardWrapper />,
              trigger: 'end-message',
            },
            {
              id: 'end-message',
              message: 'Thanks! You have seen the possibilities of the chatbot with adaptives cards!',
              end: true,
            },
          ]}
        botAvatar={botAvatar}/>
      );
    }
  }
export default SimpleForm
