import React, { Component } from 'react';
import AdaptiveCard from 'react-adaptivecards';
import axios from 'axios';


function payloads(name, siteWeb, email, phone){ 
    return (
      `
        "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
        "type": "AdaptiveCard",
        "version": "1.0",
        "body": [
          {
            "type": "Container",
            "items": [
              {
                "type": "TextBlock",
                "text": "Carte utilisateur",
                "weight": "bolder",
                "size": "medium"
              },
              {
                "type": "ColumnSet",
                "columns": [
                  {
                    "type": "Column",
                    "width": "auto",
                    "items": [
                      {
                        "type": "Image",
                        "url": "https://pbs.twimg.com/profile_images/3647943215/d7f12830b3c17a5a9e4afcc370e3a37e_400x400.jpeg",
                        "size": "small",
                        "style": "person"
                      }
                    ]
                  },
                  {
                    "type": "Column",
                    "width": "stretch",
                    "items": [
                      {
                        "type": "TextBlock",
                        "text": ${name},
                        "weight": "bolder",
                        "wrap": true
                      },
                      {
                        "type": "TextBlock",
                        "spacing": "none",
                        "isSubtle": true,
                        "wrap": true
                      }
                    ]
                  }
                ]
              }
            ]
          },
          {
            "type": "Container",
            "items": [
              
              {
                "type": "FactSet",
                "facts": [
                  {
                    "title": "E-mail:",
                    "value": ${email}
                  },
                  {
                    "title": "Tél:",
                    "value": ${phone}
                  },
                  {
                    "title": "Site web:",
                    "value": ${siteWeb}
                  }
                ]
              }
            ]
          }
        ]
      `
       
    )
  
}


class AdaptiveUserCard extends Component {

// name, siteWeb, email, phone

  constructor(props) {
    super(props);
    console.log(props.id)
    this.state = {
      loading: true,
      trigger: false,
      user:{}
    };
    this.state.user = this.generateCard();
    this.triggetNext = this.triggetNext.bind(this);
    this.result = {}
  }

  generateCard() {
    console.log('didMount' + this.props.id);
   
      axios.get(`https://jsonplaceholder.typicode.com/users/${this.props.id}`)
        
        .then(response => {

        // create an array of contacts only with relevant data
        const newUser = response.data;
        console.log(newUser)
        // create a new "State" object without mutating 
        // the original State object. 
        // const newState = Object.assign({}, this.state, {
        //   data: newUser
        // });

        // store the new state object in the component's state
        console.log(newUser.name)
        console.log(newUser.siteWeb)
        console.log(newUser.email)
        console.log(newUser.phone)
        try {
          const tmp =  '{'+payloads(newUser.name, newUser.siteWeb,newUser.email, newUser.phone)+'}' ;
          console.log('tmp: ' + tmp)
          //const obj = JSON.parse(tmp);
          this.result = tmp
          console.log('result :' + this.result)
        } catch (ex) {
          
          console.error(ex);
        }
        //this.result = payloads(newUser.name, newUser.siteWeb,newUser.email, newUser.phone);
        
        
        return newUser; 
      })
      .catch(error => console.log(error));
  }
  

  triggetNext() {
    this.setState({ trigger: true }, () => {
      this.props.triggerNextStep();
    });
  }

  render() {
     
   
    console.log(this.result) 
    
          return (
          <div className="dbpedia" style={{width:'100%'}}>
            <AdaptiveCard style={{width: '400px', border: '1px solid black'}} payload={this.result} />
          </div> );
        
    
    
  }
}

export default AdaptiveUserCard
